import base64

# Suppose this is your base64-encoded audio string.
# Replace this with your actual base64 string.
base64_audio = ""

# Decode the base64 string to binary data.
audio_data = base64.b64decode(base64_audio)

# Write the binary data to an MP3 file.
with open("output_audio.mp3", "wb") as f:
    f.write(audio_data)

print("Audio file saved as output_audio.mp3")
